        #=========== Login =========#                       
        def login(self):
                try:
                        email = self.leLoginEmail.text()
                        password = self.leLoginPassword.text()
                        
                        mydb = mc.connect(
                                host = "localhost",
                                user = "root",
                                password = "",
                                database = "inovasiKreatif"
                                )
                        
                        mycursor = mydb.cursor()
                        mycursor2 = mydb.cursor()
                        query = "SELECT id, nama, email, password, gender, alamat, noHp, status FROM akun WHERE email " \
                                "like '"+ email + "' and password like '" \
                                        + password + "'"
                        mycursor.execute(query)
                        result = mycursor.fetchone()
                        
                        if result == None:
                                print("Error: Email atau Password Salah")
                                QMessageBox.warning(self, 'Login Gagal', 'Email dan Password tidak sesuai!')
                        else:
                                print("Sukses: Login berhasil")
                                #=========== AKSES ke Dashboard ===========#
                                MainWindow.close()
                                
                                Dialog = QDialog()
                                self.form = Ui_Dialog()
                                self.form.setupUi(Dialog)
                                if result:
                                        getId = result[0]
                                        #self.form.loadProfile(getId)
                                        self.form.labelId.setText(str(getId))
                                        self.form.lineEdit.setText(result[1])
                                        self.form.lineEdit_2.setText(result[2])
                                        self.form.lineEdit_3.setText(result[3])
                                        if result[4] == 'Laki-laki':
                                                self.form.radioButton_2.setChecked(True)
                                                self.form.radioButton.setChecked(False)
                                        else:
                                                self.form.radioButton_2.setChecked(False)
                                                self.form.radioButton.setChecked(True)
                                        self.form.pAlamat_3.setText(result[5])
                                        self.form.lineEdit_4.setText(result[6])
                                        self.form.lineEdit_5.setText(result[7])
                                Dialog.show()
                                Dialog.exec_()
                                
                except mc.Error as e:
                        print("Error: Query")

